function getGender () {
    let gender = '';
    if(typeof source.person.genderCode.codeValue != 'undefined' && source.person.genderCode.codeValue){
        
        switch(source.person.genderCode.codeValue){
            case 'MALE':
                gender = 'M' ;
                break;
            case 'FEMALE':
                gender = 'V';
                break;
            default:
                gender = '';
                break;
        }
    
    }
    return gender;
}
