function convertNameConvention()
{
    let convention ="";
    if(typeof source.customFields.naamSamenstelling != 'undefined' && source.customFields.naamSamenstelling){
        switch(source.customFields.naamSamenstelling)
        {
            case "tiva":
                convention = "B";
            break;
            case "tiva-wp":
                convention = "BP";
            break;
            case "tiwp":
                convention = "P";
            break;
            case "tiwp-va":
                convention = "PB";
            break;
            default:
                convention = "B";
            break;
            
        }
    }

    return convention;
}

